import React from 'react'
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
 import { faCircleCheck, faL, faPen , faTrashCan } from '@fortawesome/free-solid-svg-icons' 
 import { useState } from 'react'
 import AddTaskForm from './componentes/AddTaskForm/AddTaskForm'
 import UpdateTaskForm from './componentes/UpdateForm/UpdateForm'
import ToDo from './componentes/ToDo/ToDo'
import UpdateForm from './componentes/UpdateForm/UpdateForm';



function App() {

  // State (to do list) state. 
  const [toDo, setToDo] = useState([]);

  // Temp State
  // State for newTask.
  const [newTask, setNewTask] = useState('');


  // State for task, maybe you modify something(edit task)
  const [updateData, setUpDateData] = useState('');

// Add task 
const addTask = () => {
    if(newTask){
      let num = toDo.length + 1;
      let newEntry = {id: num, title: newTask, status: false};
      setToDo([...toDo, newEntry])
      setNewTask('');
    }
}

// Delete 
const deleteTask = (id) => {
  let newTasks = toDo.filter(task => task.id !== id);
  setToDo(newTasks);
}

// Mark task os done or completed
const markDone = (id) => {
  let newTasks = toDo.map( task => { 
    if(task.id === id){
      return ( {...task , status: !task.status});
    }
    return task;
  })
  setToDo(newTasks);
}


// cancel

const cancelUpdate = () => {
  setUpDateData('')
}


// change task for Update 
const changeTask = (e) => {
  let newEntry = {
    id:updateData.id,
    title:e.target.value,
    status: updateData.status ? true : false
  }
  setUpDateData(newEntry);
}

// update task
const updateTask = () => {
  let filterRecords = [...toDo].filter(task => task.id !== updateData.id)
  let updateObject = [...filterRecords, updateData];
  setToDo(updateObject);
  setUpDateData('')
}





  return (


    <div className='container App'>
      <h2>To Do List</h2>


      {updateData && updateData ? (
       <UpdateForm
       updateData={updateData}
       changeTask={changeTask}
       updateTask={updateTask}
       cancelUpdate={cancelUpdate}

       /> 

      ) : (
        <AddTaskForm
        newTask={newTask}
        setNewTask={setNewTask}
        addTask={addTask}
        />
      )}



      {toDo && toDo.length ? ' ' : 'No tasks'}
      {toDo && toDo.map((task, index)=> {
        return(
          <>
            <div className='col taskBg'> 
              <div className={task.status ? 'done' : ''}>
                  <span className='taskNumber'>{index + 1}</span>
                  <span className='taskText'>{task.title}</span>
              </div>
              <div className='iconsWrap'>
                <span className='span2' title='Completed / Not Completed' 
                onClick={(e) => markDone(task.id)}>
                  <FontAwesomeIcon icon={faCircleCheck}/>
                </span>

                {task.status ? null : (
                    <span className='span2' title='Edit'
                    onClick={() => setUpDateData({
                      id:task.id,
                      title: task.title,
                      status: task.status ? true : false
                    })}>
                      <FontAwesomeIcon icon={faPen}/>
                    </span>
                )}

                <span className='span2' title='Delete' 
                onClick={() => deleteTask(task.id)}>
                  <FontAwesomeIcon icon={faTrashCan}/>
                </span>
              </div>
            </div>

          </>

        )

      })}
    </div>



)
}

export default App;
